create database [Art Gallery]
GO

USE [Art Gallery]
GO

CREATE TABLE [Role]
(
ID int primary key IDENTITY(1,1) NOT NULL,
RoleName nvarchar(100) NOT NULL,
)

CREATE TABLE [Picture]
(
ID int primary key IDENTITY(1,1) NOT NULL,
Picture  NVARCHAR(MAX) NOT NULL, 
)

CREATE TABLE [UserInfo](  /* Информация о пользователе */
	UserInfoID int primary key IDENTITY(1,1) NOT NULL,
	UserSurname nvarchar(100) NOT NULL,
	UserName nvarchar(100) NOT NULL,
	UserPatronymic nvarchar(100) NOT NULL,
	Role int foreign key references [Role](ID),
	)


CREATE TABLE [User1] /* Пользователь */
(
ID int primary key IDENTITY(1,1) NOT NULL,
	UserLogin nvarchar (50) NOT NULL,
	UserPassword nvarchar (50) NOT NULL,
	UserInfoID int foreign key references UserInfo NOT NULL,
)

CREATE TABLE [Artist] /* Художник*/
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  FIO NVARCHAR(max) NOT NULL, /* Может быть и null т.к существуют картины с неизвестными художниками*/
  YearsOfLife VARCHAR(30), /* Может быть и null т.к существуют художники с неизвестными датами рождения*/ 
)

CREATE TABLE [Genre] /* Жанр*/
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  GenreName NVARCHAR(max) NOT NULL, 
  Description NVARCHAR(max) NOT NULL , /* Описание*/
)

CREATE TABLE [Stile] /* Стиль*/
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  StileName NVARCHAR(max) NOT NULL, 
  Description NVARCHAR(max) NOT NULL , /* Описание*/
)


CREATE TABLE [Technique] /* Техника*/
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  TechniqueName NVARCHAR(max) NOT NULL, 
  Description NVARCHAR(max) NOT NULL , /* Описание*/
)

CREATE TABLE [Material] /* Матреиал*/
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  MaterialName NVARCHAR(max) NOT NULL, 
  Description NVARCHAR(max) NOT NULL, /* Описание*/
)


CREATE TABLE [PieceOfArt]	/* Предмет искусства*/
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  Picture int foreign key references [Picture](ID) ,  /* Изображение*/   
  NameOfArt NVARCHAR (MAX) NOT NULL, 
  Years nvarchar(25), 
   Artist int foreign key references [Artist](ID) NOT NULL, 
  Description NVARCHAR(max), /* Описание*/
)


CREATE TABLE [PieceOfArtHasMaterial] /*Картина+Материал*/
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  PieceOfArt int foreign key references [PieceOfArt](ID) NOT NULL, 
 Material int foreign key references [Material](ID) NOT NULL,

)

CREATE TABLE [PieceOfArtHasGenre] /* Картина+Жанр*/
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  PieceOfArt int foreign key references [PieceOfArt](ID) NOT NULL,
  Genre int foreign key references [Genre](ID) NOT NULL,
)

CREATE TABLE [PieceOfArtHasTechnique] /* Картина+Техника*/
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  PieceOfArt int foreign key references [PieceOfArt](ID) NOT NULL,
  Technique int foreign key references Technique(ID) NOT NULL,
)

CREATE TABLE [PieceOfArtHasStile]		 /* Картина+Стиль */
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  PieceOfArt int foreign key references [PieceOfArt](ID) NOT NULL,
 Stile int foreign key references Stile(ID) NOT NULL,
)


CREATE TABLE [Exhibition]				  /* Выставка */
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  ExhibitionName NVARCHAR(MAX) NOT NULL,
  Place NVARCHAR(MAX) NOT NULL,          /*Место проведения*/
  [Date] DATE NOT NULL,                           /*дата*/
  Category NVARCHAR(MAX) NOT NULL,		 /*Категория*/
  [Age limit] int NOT NULL,				 /*Возрастное ограничение*/
   Description NVARCHAR(max) NOT NULL ,  /* Описание*/
)


CREATE TABLE [Exposition]		 /* Экспозиция */
(
  ID INT primary key IDENTITY(1,1) NOT NULL,
  Exhibition int foreign key references Exhibition(ID) NOT NULL,
  PieceOfArt int foreign key references [PieceOfArt](ID) NOT NULL,
 
)

